mapper.select:
SELECT * FROM ( SELECT a.*, e.employee_name, g.id group_id, g.group_name, j.id job_id, j.job_name, '${month}' `month` FROM ${attendanceTableName} a LEFT JOIN ly_employee e ON a.employee_id = e.id LEFT JOIN ly_group g ON e.group_id = g.id LEFT JOIN ly_job j ON e.job_id = j.id ) a WHERE 1=1 AND a.enable = 1
AND a.employee_name like CONCAT('%', #{conditions.employeeName, jdbcType=VARCHAR}, '%')
AND group_id = #{conditions.groupId, jdbcType=VARCHAR}
AND job_id = #{conditions.jobId, jdbcType=VARCHAR}

mapper.insert:
INSERT INTO ${attendanceTableName} (
