mapper.select:
SELECT * FROM ( SELECT (@numInGroup := case when @gid=g.id then @numInGroup + 1 else 1 end ) num_in_group, s.*, e.employee_name, e.id_card, e.gender, j.id job_id, j.job_name, g.id group_id, g.group_name, p.id project_id, p.project_name, c.id company_id, c.company_name FROM ${salaryTableName} s LEFT JOIN ly_employee e ON s.employee_id = e.id LEFT JOIN ly_job j ON e.job_id = j.id LEFT JOIN ly_group g ON e.group_id = g.id LEFT JOIN ly_dispatch_rel dr ON g.id = dr.group_id LEFT JOIN ly_project p ON p.id = dr.project_id LEFT JOIN ly_company c ON c.id = p.company_id, (SELECT @numInGroup := 0, @gid := 0) AS numVar order by g.id, s.id ) a WHERE 1=1 AND a.enable = 1
AND employee_name like CONCAT('%', #{conditions.employeeName, jdbcType=VARCHAR}, '%')
AND group_id = #{conditions.groupId, jdbcType=VARCHAR}
AND job_id = #{conditions.jobId, jdbcType=VARCHAR}

mapper.update:
UPDATE ${salaryTableName}
social_security_allowance = #{salary.socialSecurityAllowance, jdbcType=DECIMAL}, house_fund_allowance = #{salary.houseFundAllowance, jdbcType=DECIMAL}, hot_allowance = #{salary.hotAllowance, jdbcType=DECIMAL}, other_income = #{salary.otherIncome, jdbcType=DECIMAL}, payable_salary = #{salary.payableSalary, jdbcType=DECIMAL}, personal_social_security = #{salary.personalSocialSecurity, jdbcType=DECIMAL}, personal_house_fund = #{salary.personalHouseFund, jdbcType=DECIMAL}, pay_taxes = #{salary.payTaxes, jdbcType=DECIMAL}, other_deduction = #{salary.otherDeduction, jdbcType=DECIMAL}, real_salary = #{salary.realSalary, jdbcType=DECIMAL}, company_social_security = #{salary.companySocialSecurity, jdbcType=DECIMAL}, company_house_fund = #{salary.companyHouseFund, jdbcType=DECIMAL},
WHERE id = #{salary.id, jdbcType=INTEGER}
